@app.cell
def _(backup):
    backup
    return


@app.function
def neighbor_score(matrix):
    rows, cols = matrix.shape
    result = np.zeros_like(matrix, dtype=int)
    
    for i in range(rows):
        for j in range(cols):
            for di in [-1, 0, 1]:
                for dj in [-1, 0, 1]:
                    if di == 0 and dj == 0: continue  # skip self
                    ni, nj = i + di, j + dj
                    if 0 <= ni < rows and 0 <= nj < cols:
                        result[i, j] += matrix[ni, nj]
    
    return result


@app.cell
def _():
    from scipy.ndimage import convolve

    def neighbor_score_2(matrix):
        kernel = np.ones((3, 3), dtype=int)
        kernel[1, 1] = 0  # exclude center
        return convolve(matrix, kernel, mode='constant', cval=0)
    return


@app.cell
def _():
    return


